import React, { useEffect, useState } from 'react';
import { fetchStockPrice } from '../services/stockService';

const StockPrice: React.FC<{ stockSymbol: string }> = ({ stockSymbol }) => {
    const [priceData, setPriceData] = useState<{ price: number; changePercent: number; volume: number } | null>(null);
    const [loading, setLoading] = useState<boolean>(true);
    const [error, setError] = useState<string | null>(null);

    useEffect(() => {
        const getStockPrice = async () => {
            try {
                const data = await fetchStockPrice(stockSymbol);
                setPriceData(data);
            } catch (err) {
                setError('Failed to fetch stock price data');
            } finally {
                setLoading(false);
            }
        };

        getStockPrice();
        const interval = setInterval(getStockPrice, 60000); // Refresh every minute

        return () => clearInterval(interval);
    }, [stockSymbol]);

    if (loading) return <div>Loading...</div>;
    if (error) return <div>{error}</div>;

    return (
        <div>
            <h2>Stock Price for {stockSymbol}</h2>
            {priceData && (
                <div>
                    <p>Price: ${priceData.price.toFixed(2)}</p>
                    <p>Change: {priceData.changePercent.toFixed(2)}%</p>
                    <p>Volume: {priceData.volume.toLocaleString()}</p>
                </div>
            )}
        </div>
    );
};

export default StockPrice;
